// Extract user email and recording ID
const userEmail = $input.first().headers['cf-access-authenticated-user-email'] || 'test@solfamily.group';
const recordingId = $input.first().params?.recordingId;

return {
  userEmail,
  recordingId
};

const recording = $input.first().json[0];

// Read transcript text if available
let transcriptText = '';
if (recording.text_path) {
  try {
    transcriptText = require('fs').readFileSync(recording.text_path, 'utf8');
  } catch (error) {
    console.log('Could not read transcript file:', error.message);
  }
}

// Read diarization JSON if available
let diarizationJson = null;
if (recording.diarization_json_path) {
  try {
    const diarizationContent = require('fs').readFileSync(recording.diarization_json_path, 'utf8');
    diarizationJson = JSON.parse(diarizationContent);
  } catch (error) {
    console.log('Could not read diarization file:', error.message);
  }
}

// Generate signed URLs (simplified for now - in production, these should be time-limited signed URLs)
const baseUrl = 'https://transcriber.solfamily.group';
const downloadAudioUrl = recording.audio_path ? `${baseUrl}/downloads/audio/${recording.id}` : null;
const downloadTxtUrl = recording.text_path ? `${baseUrl}/downloads/txt/${recording.id}` : null;
const downloadSrtUrl = recording.srt_path ? `${baseUrl}/downloads/srt/${recording.id}` : null;
const downloadVttUrl = recording.vtt_path ? `${baseUrl}/downloads/vtt/${recording.id}` : null;

return {
  id: recording.id,
  name: recording.name,
  mode: recording.mode,
  created_at: recording.created_at,
  duration_seconds: recording.duration_seconds,
  status: recording.status,
  transcript_text: transcriptText,
  download_audio_url: downloadAudioUrl,
  download_txt_url: downloadTxtUrl,
  download_srt_url: downloadSrtUrl,
  download_vtt_url: downloadVttUrl,
  diarization_json: diarizationJson,
  language_detected: recording.language_detected,
  speaker_count: recording.speaker_count
};
